import { Button, Form, FormControl, InputGroup } from "react-bootstrap";

const SendMessageForm = ({ sendMessage }) => {
  const [message, setMessage] = useState();

  return (
    <div>
      <h3>Hello!!</h3>
      <Form>
        <InputGroup>
          <FormControl placeholder="message..." />
          <InputGroup.Append>
            <Button variant="outline-primary">click me</Button>
            <Button variant="outline-primary">click me</Button>
          </InputGroup.Append>
        </InputGroup>
      </Form>
      {/* <Form
        onSubmit={(e) => {
          e.preventDefault();
          sendMessage(message);
          setMessage();
        }}
      >
        <InputGroup>
          <FormControl
            placeholder="message..."
            onChange={(e) => setMessage(e.target.value)}
            value={message}
          />
          <InputGroup.Append>
            <Button variant="primary" type="submit" disabled={!message}>
              Enviar
            </Button>
          </InputGroup.Append>
        </InputGroup>
      </Form> */}
    </div>
  );
};

export default SendMessageForm;
