import React from "react";
import { useEffect, useRef } from "react";

const MessageContainer = ({ messages }) => {
  const messageRef = useRef();
  var style = null;

  function MessagePosition (index){
    if(index%2==0){
      return 'chat-Esquerda';
    }else{
      return 'chat-Direita';
    }
  }

  useEffect(()=>{
    if(messageRef && messageRef.current){
      const {scrollHeigth, clientHeight} = messageRef.current;
      messageRef.current.scrollTo({
        left: 0, top: scrollHeigth - clientHeight, behavior: "smooth"
      });
    }
  },[messages]);

  return (
    <div className="message-container">
      {messages.map((m, index) => (        
        <div key={index} className={`user-message`}>
          <div className={`message bg-primary  ${MessagePosition(index)}`}>{m.message}</div>
          <div className="from-user">{m.userName}</div>
        </div>
      ))}
    </div>
  );
};

export default MessageContainer;
