import React from "react";
import { useEffect, useRef } from "react";

const MessageContainer = ({ messages, youUser: currentUser }) => {
  const messageRef = useRef();
  var style = null;

  function MessagePosition (userName){
    return userName === currentUser?'chat-Direita':'chat-Esquerda';
  }

  function TextPosition (index){
    if(index%2==0){
      return 'user-message-left';
    }else{
      return 'user-message-rigth';
    }
  }

  useEffect(()=>{
    if(messageRef && messageRef.current){
      const {scrollHeigth, clientHeight} = messageRef.current;
      messageRef.current.scrollTo({
        left: 0, top: scrollHeigth - clientHeight, behavior: "smooth"
      });
    }
  },[messages]);

  return (
    <div className="message-container">
      {messages.map((m, index) => (        
        <div key={index} className={`user-message ${TextPosition (index)}`}>
          <div className="from-user">{m.userName} diz:</div>
          <div className={`message ${MessagePosition(m.userName)}`}>{m.message}</div>
        </div>
      ))}
    </div>
  );
};

export default MessageContainer;
